--
-- Table structure for table `wtv_products`
--

CREATE TABLE IF NOT EXISTS `wtv_products` (
  `id` int(10) NOT NULL,
  `cat_id` int(3) DEFAULT NULL,
  `img` varchar(150) COLLATE utf8_unicode_ci DEFAULT NULL,
  `name` varchar(150) COLLATE utf8_unicode_ci DEFAULT NULL,
  `price` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` text COLLATE utf8_unicode_ci,
  `tinhtrang` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Dumping data for table `wtv_products`
--

INSERT INTO `wtv_products` (`id`, `cat_id`, `img`, `name`, `price`, `description`, `tinhtrang`) VALUES
(1, 62, 'p9.jpg', 'Túi xách', '20.000 đ', '<div>\r\n	Điện thoại th&ocirc;ng minh hay smartphone l&agrave; kh&aacute;i niệm để chỉ c&aacute;c loại thiết bị di động kết hợp điện thoại di động c&aacute;c chức năng điện to&aacute;n di động v&agrave;o một thiết bị. Ch&uacute;ng được ph&acirc;n biệt với điện thoại phổ th&ocirc;ng bởi khả năng phần cứng mạnh hơn v&agrave; hệ điều h&agrave;nh di động mở rộng, tạo điều kiện cho phần mềm rộng hơn, internet (bao gồm duyệt web qua băng th&ocirc;ng rộng di động) v&agrave; chức năng đa phương tiện (bao gồm &acirc;m nhạc, video, m&aacute;y ảnh v&agrave; chơi game), c&ugrave;ng với c&aacute;c chức năng ch&iacute;nh của điện thoại như cuộc gọi thoại v&agrave; nhắn tin văn bản.[1][2][3] Điện thoại th&ocirc;ng minh thường chứa một số chip IC kim loại-oxit-b&aacute;n dẫn (MOS), bao gồm c&aacute;c cảm biến kh&aacute;c nhau c&oacute; thể được tận dụng bởi phần mềm của ch&uacute;ng (chẳng hạn như từ kế, cảm biến tiệm cận, phong vũ biểu, con quay hồi chuyển hoặc gia tốc kế) v&agrave; hỗ trợ giao thức truyền th&ocirc;ng kh&ocirc;ng d&acirc;y (chẳng hạn như Bluetooth, Wi-Fi hoặc định vị vệ tinh).</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Điện thoại th&ocirc;ng minh ban đầu được tiếp thị chủ yếu hướng tới thị trường doanh nghiệp, cố gắng kết nối chức năng của thiết bị trợ l&yacute; kỹ thuật số c&aacute; nh&acirc;n PDA độc lập với hỗ trợ điện thoại di động, nhưng bị hạn chế bởi h&igrave;nh thức cồng kềnh, thời lượng pin ngắn, mạng di động tương tự chậm v&agrave; sự non nớt của c&aacute;c dịch vụ dữ liệu kh&ocirc;ng d&acirc;y. Những vấn đề n&agrave;y cuối c&ugrave;ng đ&atilde; được giải quyết với việc thu nhỏ theo cấp số nh&acirc;n v&agrave; thu nhỏ b&oacute;ng b&aacute;n dẫn MOS xuống mức dưới micromet (định luật Moore), pin lithium-ion được cải tiến, mạng dữ liệu di động kỹ thuật số nhanh hơn (định luật Edholm) v&agrave; c&aacute;c nền tảng phần mềm ho&agrave;n thiện hơn cho ph&eacute;p di động hệ sinh th&aacute;i thiết bị để ph&aacute;t triển độc lập với c&aacute;c nh&agrave; cung cấp dữ liệu.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	V&agrave;o những năm 2000, nền tảng i-mode của NTT DoCoMo, BlackBerry, nền tảng Symbian của Nokia, v&agrave; Windows Mobile bắt đầu gi&agrave;nh được sức h&uacute;t tr&ecirc;n thị trường, với c&aacute;c mẫu m&aacute;y thường c&oacute; b&agrave;n ph&iacute;m QWERTY hoặc đầu v&agrave;o m&agrave;n h&igrave;nh cảm ứng điện trở v&agrave; nhấn mạnh khả năng truy cập để gửi email v&agrave; internet kh&ocirc;ng d&acirc;y. Sau sự phổ biến ng&agrave;y c&agrave;ng tăng của iPhone v&agrave;o cuối những năm 2000, phần lớn smartphone c&oacute; kiểu d&aacute;ng mỏng, dạng thanh, với m&agrave;n h&igrave;nh điện dung lớn, hỗ trợ c&aacute;c cử chỉ đa chạm thay v&igrave; b&agrave;n ph&iacute;m vật l&yacute; v&agrave; cho ph&eacute;p người d&ugrave;ng tải xuống hoặc mua c&aacute;c ứng dụng bổ sung từ cửa h&agrave;ng tập trung v&agrave; sử dụng lưu trữ v&agrave; đồng bộ h&oacute;a đ&aacute;m m&acirc;y, trợ l&yacute; ảo cũng như c&aacute;c dịch vụ thanh to&aacute;n di động. Smartphone đ&atilde; thay thế phần lớn PDA v&agrave; PC cầm tay.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Cải tiến phần cứng v&agrave; giao tiếp kh&ocirc;ng d&acirc;y nhanh hơn (do c&aacute;c ti&ecirc;u chuẩn như LTE) đ&atilde; th&uacute;c đẩy sự ph&aacute;t triển của ng&agrave;nh c&ocirc;ng nghiệp smartphone. Trong qu&yacute; 3 năm 2012, một tỷ smartphone đ&atilde; được sử dụng tr&ecirc;n to&agrave;n thế giới.[4] Doanh số b&aacute;n smartphone to&agrave;n cầu đ&atilde; vượt qua con số doanh số của điện thoại phổ th&ocirc;ng v&agrave;o đầu năm 2013.[5]</div>\r\n', 'còn hàng'),
(2, 57, 'p13.jpg', 'Đèn ngủ 2', '20.000 đ', '<div>\r\n	Điện thoại th&ocirc;ng minh hay smartphone l&agrave; kh&aacute;i niệm để chỉ c&aacute;c loại thiết bị di động kết hợp điện thoại di động c&aacute;c chức năng điện to&aacute;n di động v&agrave;o một thiết bị. Ch&uacute;ng được ph&acirc;n biệt với điện thoại phổ th&ocirc;ng bởi khả năng phần cứng mạnh hơn v&agrave; hệ điều h&agrave;nh di động mở rộng, tạo điều kiện cho phần mềm rộng hơn, internet (bao gồm duyệt web qua băng th&ocirc;ng rộng di động) v&agrave; chức năng đa phương tiện (bao gồm &acirc;m nhạc, video, m&aacute;y ảnh v&agrave; chơi game), c&ugrave;ng với c&aacute;c chức năng ch&iacute;nh của điện thoại như cuộc gọi thoại v&agrave; nhắn tin văn bản.[1][2][3] Điện thoại th&ocirc;ng minh thường chứa một số chip IC kim loại-oxit-b&aacute;n dẫn (MOS), bao gồm c&aacute;c cảm biến kh&aacute;c nhau c&oacute; thể được tận dụng bởi phần mềm của ch&uacute;ng (chẳng hạn như từ kế, cảm biến tiệm cận, phong vũ biểu, con quay hồi chuyển hoặc gia tốc kế) v&agrave; hỗ trợ giao thức truyền th&ocirc;ng kh&ocirc;ng d&acirc;y (chẳng hạn như Bluetooth, Wi-Fi hoặc định vị vệ tinh).</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Điện thoại th&ocirc;ng minh ban đầu được tiếp thị chủ yếu hướng tới thị trường doanh nghiệp, cố gắng kết nối chức năng của thiết bị trợ l&yacute; kỹ thuật số c&aacute; nh&acirc;n PDA độc lập với hỗ trợ điện thoại di động, nhưng bị hạn chế bởi h&igrave;nh thức cồng kềnh, thời lượng pin ngắn, mạng di động tương tự chậm v&agrave; sự non nớt của c&aacute;c dịch vụ dữ liệu kh&ocirc;ng d&acirc;y. Những vấn đề n&agrave;y cuối c&ugrave;ng đ&atilde; được giải quyết với việc thu nhỏ theo cấp số nh&acirc;n v&agrave; thu nhỏ b&oacute;ng b&aacute;n dẫn MOS xuống mức dưới micromet (định luật Moore), pin lithium-ion được cải tiến, mạng dữ liệu di động kỹ thuật số nhanh hơn (định luật Edholm) v&agrave; c&aacute;c nền tảng phần mềm ho&agrave;n thiện hơn cho ph&eacute;p di động hệ sinh th&aacute;i thiết bị để ph&aacute;t triển độc lập với c&aacute;c nh&agrave; cung cấp dữ liệu.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	V&agrave;o những năm 2000, nền tảng i-mode của NTT DoCoMo, BlackBerry, nền tảng Symbian của Nokia, v&agrave; Windows Mobile bắt đầu gi&agrave;nh được sức h&uacute;t tr&ecirc;n thị trường, với c&aacute;c mẫu m&aacute;y thường c&oacute; b&agrave;n ph&iacute;m QWERTY hoặc đầu v&agrave;o m&agrave;n h&igrave;nh cảm ứng điện trở v&agrave; nhấn mạnh khả năng truy cập để gửi email v&agrave; internet kh&ocirc;ng d&acirc;y. Sau sự phổ biến ng&agrave;y c&agrave;ng tăng của iPhone v&agrave;o cuối những năm 2000, phần lớn smartphone c&oacute; kiểu d&aacute;ng mỏng, dạng thanh, với m&agrave;n h&igrave;nh điện dung lớn, hỗ trợ c&aacute;c cử chỉ đa chạm thay v&igrave; b&agrave;n ph&iacute;m vật l&yacute; v&agrave; cho ph&eacute;p người d&ugrave;ng tải xuống hoặc mua c&aacute;c ứng dụng bổ sung từ cửa h&agrave;ng tập trung v&agrave; sử dụng lưu trữ v&agrave; đồng bộ h&oacute;a đ&aacute;m m&acirc;y, trợ l&yacute; ảo cũng như c&aacute;c dịch vụ thanh to&aacute;n di động. Smartphone đ&atilde; thay thế phần lớn PDA v&agrave; PC cầm tay.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Cải tiến phần cứng v&agrave; giao tiếp kh&ocirc;ng d&acirc;y nhanh hơn (do c&aacute;c ti&ecirc;u chuẩn như LTE) đ&atilde; th&uacute;c đẩy sự ph&aacute;t triển của ng&agrave;nh c&ocirc;ng nghiệp smartphone. Trong qu&yacute; 3 năm 2012, một tỷ smartphone đ&atilde; được sử dụng tr&ecirc;n to&agrave;n thế giới.[4] Doanh số b&aacute;n smartphone to&agrave;n cầu đ&atilde; vượt qua con số doanh số của điện thoại phổ th&ocirc;ng v&agrave;o đầu năm 2013.[5]</div>\r\n', 'còn hàng'),
